mapper.update:
UPDATE qnaBoard SET
 hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,name,subject,content,hit,TO_CHAR(regdate,'YYYY-MM-DD') as dbday,group_id
FROM qnaBoard
WHERE no=#{no}

mapper.update:
UPDATE qnaBoard SET
anok='n'
WHERE group_id=#{group_id}

mapper.select:
SELECT no,name,subject,TO_CHAR(regdate,'YYYY-MM-DD HH24:MI:SS') as dbday, content,group_id
FROM qnaBoard
WHERE group_id=#{group_id}

mapper.update:
UPDATE qnaBoard SET
anok='y'
WHERE group_id=#{group_id}
